INSERT INTO combined(	sourcecode,	statecode,	state,	country,	positive,	positiveincrease,	negative,	negativeincrease,	totaltestresults,	totaltestresultsincrease,	hospitalized,	hospitalizedincrease,	death,	deathincrease,	recovered,	recoveredincrease,	date)SELECT	'CTP' as sourcecode,	state.ansi,	state.name,	country,	positive,	positiveincrease,	negative,	negativeincrease,	totaltestresults,	totaltestresultsincrease,	hospitalized,	hospitalizedincrease,	death,	deathincrease,	null as recovered,	null as recoveredincrease,	dateFROM ctp_statesdaily ctpJOIN state on (ctp.state = state.ansi);