INSERT INTO combined(sourcecode,state,country,datechecked,date)SELECT DISTINCT'JH_G' as sourcecode,state,country,date as datechecked,to_date(date,'mm/dd/yy') as dateFROM jh_timeseriesWHERE country != 'United States';UPDATE combinedSET confirmed = v1.ctFROM( SELECT * FROM jh_timeseriesWHERE type = 'C') AS v1WHERE (combined.state = v1.state OR (combined.state is null AND v1.state is null))AND combined.country = v1.countryAND combined.datechecked = v1.date;UPDATE combinedSET death = v1.ctFROM( SELECT * FROM jh_timeseriesWHERE type = 'D') AS v1WHERE (combined.state = v1.state OR (combined.state is null AND v1.state is null))AND combined.country = v1.countryAND combined.datechecked = v1.date;UPDATE combinedSET recovered = v1.ctFROM( SELECT * FROM jh_timeseriesWHERE type = 'R') AS v1WHERE (combined.state = v1.state OR (combined.state is null AND v1.state is null))AND combined.country = v1.countryAND combined.datechecked = v1.date;