INSERT INTO combined(sourcecode,city,state,statecode,country,datechecked,date)SELECT DISTINCT'JH' as sourcecode,city,state.name,ansi as statecode,country,date as datechecked,to_date(date,'yyyy-mm-dd') as dateFROM jh_us_timeseries tsJOIN state on (ts.state = state.name);UPDATE combinedSET positive = v1.ctFROM( SELECT * FROM jh_us_timeseriesWHERE type = 'C') AS v1WHERE combined.state = v1.stateAND combined.country = v1.countryAND combined.city = v1.cityAND combined.datechecked = v1.date;UPDATE combinedSET death = v1.ctFROM( SELECT * FROM jh_us_timeseriesWHERE type = 'D') AS v1WHERE combined.state = v1.stateAND combined.country = v1.countryAND combined.city = v1.cityAND combined.datechecked = v1.date;